import React from 'react';
import { useState } from 'react';
import { Box, Heading, Center, Input, IconButton, Icon, HStack, VStack, Checkbox, Text } from 'native-base';
import { Ionicons, MaterialCommunityIcons } from "@expo/vector-icons"

import AddButton from './AddButton';
import TaskInput from './TaskInput';

export default function AddTaskBox() {
  const [inputValue, setInputValue] = useState("")
  const [list, setList] = useState([])

  const addTask = (task) => {
    setList([
      ...list, 
      {
        task,
        isCompleted: false
      },
    ])
  }

  const handleStatusChange = (index) => {
    console.log(index);
    const temp = list.map((item, itemI) =>
      itemI !== index ? item : { ...item, isCompleted: !item.isCompleted }
    )
    setList(temp)
  }

    return (
    <Box width="100%">
      <Center flex={1} px="3">
        <Heading mb="5" color="orange.500">To do List</Heading>
        <VStack space={4}>
          <HStack space={3}>
            <Input 
              flex={2}
              color="orange.500"
              borderColor="orange.500"
              _focus={{
                borderColor: "orange.600:alpha.20",
              }}
              onChangeText={(v) => setInputValue(v)}
              value={inputValue}
              placeholder="Add Task"
            />
            <IconButton
              onPress={() => {
                addTask(inputValue)
                setInputValue("")
              }}
              icon={<Icon as={Ionicons} name="ios-add-circle" />}
              borderRadius="full"
              _icon={{
                color: "orange.500",
                size: "md",
              }}
              _hover={{
                bg: "orange.600:alpha.20",
              }}
              _pressed={{
                bg: "orange.600:alpha.20",
                _icon: {
                  name: "ios-add-circle",
                },
                _ios: {
                  _icon: {
                    size: "3xl",
                  },
                },
              }}
              _ios={{
                _icon: {
                  size: "3xl",
                },
              }}
            />


          </HStack>

          {list.map((item, itemI) => (
            <HStack 
              space={5}
              key={item.task + itemI.toString()}
            >
              <Checkbox
                colorScheme="orange"
                isChecked={item.isCompleted}
                onChange={() => handleStatusChange(itemI)}
                value={item.task}
                icon={<Icon as={<Ionicons name="md-checkmark-done" />} />}
                _hover={{
                  bg: "orange.600:alpha.20",
                  borderColor: "orange.500"
                }}
              >
                <Text
                  mx={3}
                  strikeThrough={item.isCompleted}
                  _light={{
                    color: item.isCompleted ? "gray.400" : "coolGray.800",
                  }}
                >
                  {item.task}
                </Text>
              </Checkbox>
              <IconButton
                size="sm"
                colorScheme="orange"
                color="orange.500"
                icon={
                  <Icon
                    as={MaterialCommunityIcons}
                    name="delete"
                    size="lg"
                    color="trueGray.400"
                    _hover={{
                      bg: "orange.600:alpha.20"
                    }}
                    _focus={{
                      bg: "orange.600:alpha.20",
                    }}
                  />

                }
                // onPress={() => handleDelete(itemI)}
              />
            </HStack>
          ))}
        </VStack>


      </Center>
    </Box>
    );
}
