import React from 'react';
import { useState } from 'react';
import { Box, Heading, Center, Input, IconButton, Icon, HStack } from 'native-base';
import { Ionicons, Entypo } from "@expo/vector-icons"

import AddButton from './AddButton';
import TaskInput from './TaskInput';

export default function AddTaskBox() {
  const [inputValue, setInputValue] = useState("")

  const handleInput = (e) => {
    setInputValue(e);
    getInput(inputValue);
  }
    return (
    <Box width="100%">
      <Center flex={1} px="3">
        <Heading mb="7" color="red">To do List</Heading>
        <HStack space={3}>
          <Input 
            flex={2}
            onChangeText={(v) => setInputValue(v)}
            value={inputValue}
            placeholder="Add Task"
          />
          <Ionicons name="ios-add-circle" size={24} color="purple" />
          <IconButton
            icon={<Icon as={Entypo} name="emoji-happy" />}
            borderRadius="full"
            _icon={{
              color: "orange.500",
              size: "md",
            }}
            _hover={{
              bg: "orange.600:alpha.20",
            }}
            _pressed={{
              bg: "orange.600:alpha.20",
              _icon: {
                name: "emoji-flirt",
              },
              _ios: {
                _icon: {
                  size: "2xl",
                },
              },
            }}
            _ios={{
              _icon: {
                size: "2xl",
              },
            }}
          />
        </HStack>

      </Center>
    </Box>
    );
}
