import React from 'react';
import { useState } from 'react';
import { Box, Heading, Center, Input, IconButton, Icon, HStack, VStack, Checkbox, Text } from 'native-base';
import { Ionicons, Entypo } from "@expo/vector-icons"

import AddButton from './AddButton';
import TaskInput from './TaskInput';

export default function AddTaskBox() {
  const [inputValue, setInputValue] = useState("")
  const [list, setList] = useState([])

  const addTask = (task) => {
    setList([
      ...list, 
      {}
    ])
  }
    return (
    <Box width="100%">
      <Center flex={1} px="3">
        <Heading mb="5" color="orange.500">To do List</Heading>
        <VStack space={4}>
          <HStack space={3}>
            <Input 
              flex={2}
              color="orange.500"
              borderColor="orange.500"
              _focus={{
                borderColor: "orange.600:alpha.20",
              }}
              onChangeText={(v) => setInputValue(v)}
              value={inputValue}
              placeholder="Add Task"
            />
            <IconButton
              icon={<Icon as={Ionicons} name="ios-add-circle" />}
              borderRadius="full"
              _icon={{
                color: "orange.500",
                size: "md",
              }}
              _hover={{
                bg: "orange.600:alpha.20",
              }}
              _pressed={{
                bg: "orange.600:alpha.20",
                _icon: {
                  name: "ios-add-circle",
                },
                _ios: {
                  _icon: {
                    size: "3xl",
                  },
                },
              }}
              _ios={{
                _icon: {
                  size: "3xl",
                },
              }}
            />


          </HStack>

          {list.map((item, itemI) => (
            <HStack space={5}>
              <Checkbox>
                <Text>{list.task}</Text>
              </Checkbox>
            </HStack>
          ))}
        </VStack>


      </Center>
    </Box>
    );
}
